	 * names, checking to see whether a matching key exists in populated
	 * array. If so the setByName() method is called for that column.
	 *
	 * You can specify the key type of the array by additionally passing one
	 * of the class type constants BasePeer::TYPE_PHPNAME, BasePeer::TYPE_STUDLYPHPNAME,
	 * BasePeer::TYPE_COLNAME, BasePeer::TYPE_FIELDNAME, BasePeer::TYPE_NUM.
	 * The default key type is the column's phpname (e.g. 'AuthorId')
	 *
	 * @param      array  $arr     An array to populate the object from.
	 * @param      string $keyType The type of keys the array uses.
	 * @return     void
	 */
	public function fromArray($arr, $keyType = BasePeer::TYPE_PHPNAME)
	{
		$keys = TransactionPeer::getFieldNames($keyType);

		if (array_key_exists($keys[0], $arr)) $this->setId($arr[$keys[0]]);
		if (array_key_exists($keys[1], $arr)) $this->setAmount($arr[$keys[1]]);
		if (array_key_exists($keys[2], $arr)) $this->setDescription($arr[$keys[2]]);
		if (array_key_exists($keys[3], $arr)) $this->setOrderId($arr[$keys[3]]);
		if (array_key_exists($keys[4], $arr)) $this->setCustomerId($arr[$keys[4]]);
		if (array_key_exists($keys[5], $arr)) $this->setTransactionStatusId($arr[$keys[5]]);
		if (array_key_exists($keys[6], $arr)) $this->setCreatedAt($arr[$keys[6]]);
		if (array_key_exists($keys[7], $arr)) $this->setAgentCompanyId($arr[$keys[7]]);
		if (array_key_exists($keys[8], $arr)) $this->setCommissionAmount($arr[$keys[8]]);
	}

	/**
	 * Build a Criteria object containing the values of all modified columns in this object.
	 *
	 * @return     Criteria The Criteria object containing all modified values.
	 */
	public function buildCriteria()
	{
		$criteria = new Criteria(TransactionPeer::DATABASE_NAME);

		if ($this->isColumnModified(TransactionPeer::ID)) $criteria->add(TransactionPeer::ID, $this->id);
		if ($this->isColumnModified(TransactionPeer::AMOUNT)) $criteria->add(TransactionPeer::AMOUNT, $this->amount);
		if ($this->isColumnModified(TransactionPeer::DESCRIPTION)) $criteria->add(TransactionPeer::DESCRIPTION, $this->description);
		if ($this->isColumnModified(TransactionPeer::ORDER_ID)) $criteria->add(TransactionPeer::ORDER_ID, $this->order_id);
		if ($this->isColumnModified(TransactionPeer::CUSTOMER_ID)) $criteria->add(TransactionPeer::CUSTOMER_ID, $this->customer_id);
		if ($this->isColumnModified(TransactionPeer::TRANSACTION_STATUS_ID)) $criteria->add(TransactionPeer::TRANSACTION_STATUS_ID, $this->transaction_status_id);
		if ($this->isColumnModified(TransactionPeer::CREATED_AT)) $criteria->add(TransactionPeer::CREATED_AT, $this->created_at);
		if ($this->isColumnModified(TransactionPeer::AGENT_COMPANY_ID)) $criteria->add(TransactionPeer::AGENT_COMPANY_ID, $this->agent_company_id);
		if ($this->isColumnModified(TransactionPeer::COMMISSION_AMOUNT)) $criteria->add(TransactionPeer::COMMISSION_AMOUNT, $this->commission_amount);

		return $criteria;
	}

	/**
	 * Builds a Criteria object containing the primary key for this object.
	 *
	 * Unlike buildCriteria() this method includes the primary key values regardless
	 * of whether or not they have been modified.
	 *
	 * @return     Criteria The Criteria object containing value(s) for primary key(s).
